import React, { Component } from 'react';
import './index.css';

class Paginate extends Component {
    render() {
        let pageIndex = [];
        const filteredList = this.props.filter ? this.props.bookList.filter(book => book.is_reserved) : this.props.bookList ;
        console.log(filteredList)
        // if (this.props.filter) {
        //     reservedList.map((book, index) => {
        //         console.log('checking,', book)
        //         if ()
        //         // pageIndex.push(index+1);
        //     })
        // } else {
            for (let page=1; page <= Math.ceil(filteredList/3); page++) {
                pageIndex.push(page);
            }
        // }
        console.log(pageIndex)

        return (
            <div className="paginate-container">
                {
                    pageIndex.map((number) => {
                        return  <button key={number}
                                        type="button"
                                        className={this.props.pageNumber === number ? "current-page" : "page"}
                                        onClick={() => this.props.changePage(number)}
                                >
                                    {number}
                                </button>
                    })
                }
            </div>
        )
    }

}

export default Paginate;
