import React, { Component } from 'react';
import Filter from '../components/Filter';
import BookList from '../components/BookList';
import Paginate from '../components/Paginate';
import Search from '../components/Search';
import './index.css';

class Library extends Component {
    constructor(props) {
        super(props);
        this.state = {
            bookList: [],
            pageNumber: 1
        };
        this.getBooks = this.getBooks.bind(this);
    }

    componentWillMount() {
        this.getBooks();
    }

    getBooks() {
        fetch('http://127.0.0.1:8000/library/books')
        .then(response => response.json())
        .then(data => {
            this.setState({
                bookList: data
            })
        })
    }

    changePage() {
        console.log('hello')
    }

    render() {
        console.log('home', this.state.bookList)
        return (
            <div id="home">
                <h1>Saasvile Public Library</h1>
                <Search />
                <Filter />
                <BookList bookList={this.state.bookList} pageNumber={this.state.pageNumber} />
                {
                    this.state.bookList.length
                    ? <Paginate bookList={this.state.bookList} pageNumber={this.state.pageNumber} changePage={() => this.changePage()} />
                    : null
                }
            </div>
        )
    }
}

export default Library;
