import React from 'react';
import './index.css';

const Paginate = ({bookList, pageNumber, changePage}) => {
    let pageIndex = [];
    for (let page=1; page <= Math.ceil(bookList.length/3); page++) {
        pageIndex.push(page);
    }

    return (
        <div className="paginate-container">
            {
                pageIndex.map((number) => {
                    return  <button key={number}
                                    className={pageNumber === number ? "current-page" : "page"}
                                    onClick={() => changePage(number)}
                            >
                                {number}
                            </button>
                })
            }
        </div>
    )
}

export default Paginate;
