import React, { Component } from 'react';
import Filter from '../components/Filter';
import BookList from '../components/BookList';
import Paginate from '../components/Paginate';
import Search from '../components/Search';
import './index.css';

class Library extends Component {
    constructor(props) {
        super(props);
        this.state = {
            bookList: [],
            pageNumber: 1,
            filter: 0, // 0 = all books, 1 = reserved books
            form: {
                search: ''
            }
        };
        this.getBooks = this.getBooks.bind(this);
    }

    componentWillMount() {
        this.getBooks();
    }

    getBooks() {
        fetch('http://127.0.0.1:8000/library/books')
        .then(response => response.json())
        .then(data => {
            this.setState({
                bookList: data
            })
        })
    }

    changePage(number) {
        this.setState({
            pageNumber: number
        })
    }

    getReservedBooks(number) {
        this.setState({
            filter: number
        })
    }

    handleSearch(e) {
        const name = e.target.name;
        const value = e.target.value;

        this.setState({
            form: {
                ...this.state.form,
                [name]: value
            }
        })

        let searchedList = this.state.bookList.filter(book => book.title.toLowerCase().indexOf(value.toLowerCase()) !== -1);
        console.log('=== 0 ===', searchedList);
    }

    render() {

        return (
            <div id="home">
                <h1>Saasvile Public Library</h1>
                <Search handleSearch={this.handleSearch.bind(this)} />
                <Filter filter={this.state.filter} getFilter={this.getReservedBooks.bind(this)} />
                <BookList   bookList={this.state.bookList} 
                            pageNumber={this.state.pageNumber}
                            filter={this.state.filter}
                            search={this.state.form} />
                {
                    this.state.bookList.length
                    ? <Paginate bookList={this.state.bookList} 
                                pageNumber={this.state.pageNumber} 
                                changePage={this.changePage.bind(this)}
                                filter={this.state.filter} />
                    : null
                }
            </div>
        )
    }
}

export default Library;
