import React from 'react';
import BookCard from '../BookCard';
import './index.css';

const BookList = ({bookList, pageNumber, filter}) => {
    const maxPageNumber = 3 * pageNumber;
    const minPageNumber = 3 * pageNumber - 2;

    if (filter) {
        return (
            <div className="book-container">
                {
                    bookList.filter(book => book.id <= maxPageNumber && book.id >= minPageNumber).map((book, index) => {
                        return (
                            <BookCard key={index} book={book} />
                        )
                    })
                }
            </div>
        );
    } else {
        return (
            <div className="book-container">
                {
                    bookList.map((book, index) => {
                        return (
                            <BookCard key={index} book={book} />
                        )
                    })
                }
            </div>
        );
    }
};

export default BookList;
